import{_ as o,a,b as e}from"./chunks/data-analysis-dataframe-example.C0HheKt2.js";import{_ as r,a as l}from"./chunks/data-analysis-kandy-example.Cg4ZohPS.js";import{_ as i,c as n,o as p,ag as s}from"./chunks/framework.Bksy39di.js";const y=JSON.parse('{"title":"Kotlinによるデータ分析","description":"","frontmatter":{},"headers":[],"relativePath":"ja/kotlin/data-analysis-overview.md","filePath":"ja/kotlin/data-analysis-overview.md","lastUpdated":1754307826000}'),k={name:"ja/kotlin/data-analysis-overview.md"};function h(d,t,b,K,m,f){return p(),n("div",null,t[0]||(t[0]=[s('<h1 id="kotlinによるデータ分析" tabindex="-1">Kotlinによるデータ分析 <a class="header-anchor" href="#kotlinによるデータ分析" aria-label="Permalink to &quot;Kotlinによるデータ分析&quot;">​</a></h1><p>データの探索と分析は、毎日行うことではないかもしれませんが、ソフトウェア開発者として必要な極めて重要なスキルです。</p><p>データ分析が鍵となるソフトウェア開発業務について考えてみましょう。デバッグ時にコレクションの内部構造を分析したり、メモリダンプやデータベースを掘り下げたり、REST APIと連携する際に大量のデータを含むJSONファイルを受信したりすることなどが挙げられます。</p><p>Kotlinの探索的データ分析（EDA）ツールである<a href="#notebooks">Kotlin Notebook</a>、<a href="#kotlin-dataframe">Kotlin DataFrame</a>、<a href="#kandy">Kandy</a>を使用すると、分析スキルを向上させ、さまざまなシナリオに対応するための豊富な機能を利用できます。</p><ul><li><p><strong>さまざまな形式のデータを読み込み、変換し、視覚化する:</strong> Kotlin EDAツールを使用すると、データのフィルタリング、並べ替え、集計などのタスクを実行できます。これらのツールは、CSV、JSON、TXTなどのさまざまなファイル形式からIDE内でデータをシームレスに読み取ることができます。</p><p>当社のプロットツールであるKandyは、データセットを視覚化し、洞察を得るための幅広い種類のチャートを作成できます。</p></li><li><p><strong>リレーショナルデータベースに保存されたデータを効率的に分析する:</strong> Kotlin DataFrameはデータベースとシームレスに統合し、SQLクエリに似た機能を提供します。さまざまなデータベースからデータを直接取得、操作、視覚化できます。</p></li><li><p><strong>Web APIからリアルタイムおよび動的なデータセットを取得し、分析する:</strong> EDAツールの柔軟性により、OpenAPIのようなプロトコルを介して外部APIとの統合が可能です。この機能は、Web APIからデータをフェッチし、必要に応じてデータをクリーンアップおよび変換するのに役立ちます。</p></li></ul><p>Kotlinデータ分析ツールを試してみませんか？</p><p><a href="/ja/kotlin/get-started-with-kotlin-notebooks"><img src="'+o+'" width="600" alt="Get started with Kotlin Notebook" style=""></a></p><p>Kotlinデータ分析ツールを使用すると、データの最初から最後までをスムーズに処理できます。Kotlin Notebookのシンプルなドラッグ＆ドロップ機能でデータを簡単に取得し、数行のコードでクリーンアップ、変換、視覚化できます。さらに、数回クリックするだけで出力チャートをエクスポートできます。</p><p><img src="'+r+'" alt="Kotlin Notebook" width="700"></p><h2 id="notebooks" tabindex="-1">Notebooks <a class="header-anchor" href="#notebooks" aria-label="Permalink to &quot;Notebooks&quot;">​</a></h2><p><em>Notebooks</em> は、コード、グラフィック、テキストを単一の環境に統合するインタラクティブなエディターです。ノートブックを使用すると、コードセルを実行し、すぐにその出力を確認できます。</p><p>Kotlinは、<a href="#kotlin-notebook">Kotlin Notebook</a>、<a href="#kotlin-notebooks-in-datalore">Datalore</a>、<a href="#jupyter-notebook-with-kotlin-kernel">Kotlin-Jupyter Notebook</a>など、さまざまなノートブックソリューションを提供しており、データの取得、変換、探索、モデリングなどに便利な機能を提供します。これらのKotlinノートブックソリューションは、当社の<a href="https://github.com/Kotlin/kotlin-jupyter" target="_blank" rel="noreferrer">Kotlin Kernel</a>に基づいています。</p><p>Kotlin Notebook、Datalore、Kotlin-Jupyter Notebook間でコードをシームレスに共有できます。いずれかのKotlinノートブックでプロジェクトを作成し、互換性の問題を気にすることなく別のノートブックで作業を継続できます。</p><p>強力なKotlinノートブックの機能とKotlinでのコーディングの利点を活用してください。Kotlinはこれらのノートブックと統合されており、データ管理や同僚との発見の共有を支援し、データサイエンスと機械学習のスキルを向上させます。</p><p>さまざまなKotlinノートブックソリューションの機能を活用し、プロジェクトの要件に最適なものを選択してください。</p><p><img src="'+a+'" alt="Kotlin Notebook" width="700"></p><h3 id="kotlin-notebook" tabindex="-1">Kotlin Notebook <a class="header-anchor" href="#kotlin-notebook" aria-label="Permalink to &quot;Kotlin Notebook&quot;">​</a></h3><p><a href="/ja/kotlin/kotlin-notebook-overview">Kotlin Notebook</a>は、IntelliJ IDEAのプラグインであり、Kotlinでノートブックを作成できます。一般的なIDEの機能をすべて備えたIDEエクスペリエンスを提供し、リアルタイムのコードインサイトとプロジェクト統合を実現します。</p><h3 id="dataloreでのkotlinノートブック" tabindex="-1">DataloreでのKotlinノートブック <a class="header-anchor" href="#dataloreでのkotlinノートブック" aria-label="Permalink to &quot;DataloreでのKotlinノートブック&quot;">​</a></h3><p><a href="https://datalore.jetbrains.com/" target="_blank" rel="noreferrer">Datalore</a>を使用すると、追加のインストールなしでブラウザでKotlinをすぐに使用できます。ノートブックを共有してリモートで実行したり、他のKotlinノートブックとリアルタイムで共同作業したり、コード作成時にスマートなコーディング支援を受けたり、インタラクティブまたは静的なレポートを介して結果をエクスポートしたりすることもできます。</p><h3 id="kotlin-kernelを備えたjupyter-notebook" tabindex="-1">Kotlin Kernelを備えたJupyter Notebook <a class="header-anchor" href="#kotlin-kernelを備えたjupyter-notebook" aria-label="Permalink to &quot;Kotlin Kernelを備えたJupyter Notebook&quot;">​</a></h3><p><a href="https://jupyter.org/" target="_blank" rel="noreferrer">Jupyter Notebook</a>は、コード、視覚化、Markdownテキストを含むドキュメントを作成および共有できるオープンソースのWebアプリケーションです。<a href="https://github.com/Kotlin/kotlin-jupyter" target="_blank" rel="noreferrer">Kotlin-Jupyter</a>は、Jupyter環境内でKotlinの能力を活用するために、Jupyter NotebookにKotlinサポートをもたらすオープンソースプロジェクトです。</p><h2 id="kotlin-dataframe" tabindex="-1">Kotlin DataFrame <a class="header-anchor" href="#kotlin-dataframe" aria-label="Permalink to &quot;Kotlin DataFrame&quot;">​</a></h2><p><a href="https://kotlin.github.io/dataframe/overview.html" target="_blank" rel="noreferrer">Kotlin DataFrame</a>ライブラリは、Kotlinプロジェクトで構造化データを操作できます。データの作成とクリーンアップから、詳細な分析、特徴量エンジニアリングまで、このライブラリが対応します。</p><p>Kotlin DataFrameライブラリを使用すると、CSV、JSON、XLS、XLSXなど、さまざまなファイル形式を扱えます。このライブラリは、SQLデータベースやAPIに接続できる機能により、データ取得プロセスも容易にします。</p><p><img src="'+e+'" alt="Kotlin DataFrame" width="700"></p><h2 id="kandy" tabindex="-1">Kandy <a class="header-anchor" href="#kandy" aria-label="Permalink to &quot;Kandy&quot;">​</a></h2><p><a href="https://kotlin.github.io/kandy/welcome.html" target="_blank" rel="noreferrer">Kandy</a>は、さまざまな種類のチャートをプロットするための強力で柔軟なDSLを提供するオープンソースのKotlinライブラリです。このライブラリは、データを視覚化するためのシンプルで慣用的、読みやすく、型安全なツールです。</p><p>Kandyは、Kotlin Notebook、Datalore、Kotlin-Jupyter Notebookとシームレスに統合されています。KandyライブラリとKotlin DataFrameライブラリを組み合わせて、さまざまなデータ関連タスクを簡単に完了することもできます。</p><p><img src="'+l+'" alt="Kandy" width="700"></p><h2 id="次のステップ" tabindex="-1">次のステップ <a class="header-anchor" href="#次のステップ" aria-label="Permalink to &quot;次のステップ&quot;">​</a></h2><ul><li><a href="/ja/kotlin/get-started-with-kotlin-notebooks">Kotlin Notebookを始める</a></li><li><a href="/ja/kotlin/data-analysis-work-with-data-sources">Kotlin DataFrameライブラリを使用してデータを取得および変換する</a></li><li><a href="/ja/kotlin/data-analysis-visualization">Kandyライブラリを使用してデータを視覚化する</a></li><li><a href="/ja/kotlin/data-analysis-libraries">データ分析のためのKotlinおよびJavaライブラリについて詳しく学ぶ</a></li></ul>',32)]))}const N=i(k,[["render",h]]);export{y as __pageData,N as default};
